import base64
import time
import os
from colorama import init, Fore, Style
from pystyle import Center

import re

def strip_ansi(s: str) -> str:
    """Removes ANSI escape sequences from a string."""
    ansi_escape = re.compile(r'\x1B(?:[@-Z\\-_]|\[[0-?]*[ -/]*[@-~])')
    return ansi_escape.sub('', s)

def virtual_center_ansi(s: str) -> str:
    """
    Centers an ANSI string based on its visible length, treating it as if it had
    a virtual length.

    Args:
        s (str): The ANSI string to center.
       
    Returns:
        str: The centered ANSI string with ANSI codes preserved.
    """
    visible_string = strip_ansi(s)
    virtual_length = len(visible_string)

    total_padding = max(0, os.get_terminal_size().columns - 2 - int(virtual_length))
    left_padding = total_padding // 2
    right_padding = total_padding - left_padding

    return ' ' * left_padding + s + ' ' * right_padding


def banner() -> str:
    banner = (
        ""
    )
    return base64.b64decode(banner).decode('utf-8')

def show_banner(sleep_time: int = 2) -> None:
    init()
    print(Center.XCenter("DJ Stomp 2025"))
    time.sleep(sleep_time)
    _ = [print(virtual_center_ansi(ea)) for ea in ["", *banner().splitlines()]] or print("\n")
    time.sleep(sleep_time)
    print(Center.XCenter("No Rights Reserved"))
    time.sleep(sleep_time*2)

if __name__ == "__main__":
    show_banner()